import React, { Component } from "react";
import Iframe from "react-iframe";
import "./playvideo.css";
export default class PlayVideo extends Component {

    state={
        videosnippet:[]
    }

    videourl = `http://www.youtube.com/embed/${this.props.video.id}`

videoMapping = () =>{
     console.log(this.props.video.snippet.title)
}

    componentWillMount() {
        this.videoMapping()
    }


  render() {
      console.log(this.state.videosnippet)
      const{statistics} = this.props.video
   const {snippet} = this.props.video
    return (
      <div className="playvideocomponent">
        <div className="videoplaytext">
    <h2>{snippet.title}</h2>
        </div>
        <Iframe
          url={this.videourl}
          width="980px"
          height="540px"
          id="myId"
          className="myClassname"
          display="initial"
          position="relative"
        />
        <div className="videobottom">
          <div className="videoinfo">
    <h2 className="view-date">{statistics.viewCount} ~ {snippet.publishedAt.slice(0,10)}</h2>
    <h2>Channel Name:{snippet.channelTitle}</h2>
          </div>

          <div className="likescount">
    <h2>Like:{statistics.likeCount}</h2>
    <h2>Dislike:{statistics.dislikeCount}</h2>
          </div>
        </div>
      </div>
    );
  }
}
