import React, { Component } from 'react'
import './App.css';
import Header from './components/header/Header';
import Sidebar from './components/sidebar/Sidebar';
import RecommendedVideos from './components/recommendedVideos/RecommendedVideos';
import PlayVideo from "./components/recommendedVideos/videoCart/playVideo/PlayVideo"
import axios from "axios";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
} from "react-router-dom";


export default class Dashboard extends Component {

    state = {
        ytvideos: [],
        playvideo:{}
    }

    getSearchQuery = (e) => {
console.log(e.target.value)
    }



    searchVideos = (() => {
        axios.get("https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=bakinbeyler&key=AIzaSyAGN-GixhofNuWJ-9_d8UmHm23QEbpngCA")
            .then(resp => this.setState({
                ytvideos: resp.data.items
            }))
    })

    getVideoId = (e,video) => {
        this.setState({
            playvideo:video
        })
    }
    componentDidMount() {
        console.log(this.state.ytvideos)

        const popularVideos = (() => {
            axios.get("https://youtube.googleapis.com/youtube/v3/videos?part=snippet%2CcontentDetails%2Cstatistics&chart=mostPopular&regionCode=US&maxResults=50&key=AIzaSyDH4v51BTNyWPGlnp8eHPGui2Bu3EhfTas")
                .then(resp => this.setState({
                    ytvideos: resp.data.items
                }))
        })()
    }



    render() {
        return (
            <div>
                <Header getSearchQuery={this.getSearchQuery}/>
                <div className="app-page">
                <Sidebar />
                <Router>
                    <Switch>
                    <Route exact path="/">
                    <RecommendedVideos videos={this.state.videos} ytvideos={this.state.ytvideos} getVideoId={this.getVideoId}/>
                    </Route>
                   <Route exact path="/play">
                    <PlayVideo video={this.state.playvideo}/>
                   </Route>
                    </Switch>
                </Router>
                </div>

            </div>

        )
    }
}
