import React, { Component } from "react";
import Iframe from "react-iframe";
import "./playvideo.css";
import { Link } from "react-router-dom";


export default class PlayVideo extends Component {

    state={
        videosnippet:[],
        statisticsIsProperty:true
    }

    videourl = `https://www.youtube.com/embed/${this.props.video.id}`

videoMapping = () =>{
     console.log(this.props.video.snippet.title)
}

    componentWillMount() {
        this.videoMapping()
    }

    componentDidMount() {
      this.props.video.hasOwnProperty("statistics") ? this.setState({statisticsIsProperty:true}) : this.setState({statisticsIsProperty:false})
    }



  render() {
      console.log(this.state.videosnippet)
      const{statistics} = this.props.video
   const {snippet} = this.props.video
    return (
      <div className="playvideocomponent">

        <div className="videoplaytext">
    <h2>{snippet.title}</h2>
        </div>

        <Iframe
          url={this.videourl}
          width="1280px"
          height="740px"
          id="myId"
          className="myClassname"
          display="initial"
          position="relative"
        />
        <div className="videobottom">
          <div className="videoinfo">
  <h2 className="view-date">{ this.state.statisticsIsProperty ? statistics.viewCount : null} views ~ {snippet.publishedAt.slice(0,10)}</h2>
    <h2>Channel Name:{snippet.channelTitle}</h2>
          </div>

          <div className="likescount">
    <h2>Like:{this.state.statisticsIsProperty ? statistics.likeCount : null}</h2>
    <h2>Dislike:{this.state.statisticsIsProperty ? statistics.dislikeCount : null}</h2>
          </div>
        </div>
      </div>
    );
  }
}
